export const add=(task)=>{
    return {
        type:'ADD',
        payload:task,
        id: Math.random()
    }
}

export const done=()=>{
    return {
        type: 'DONE'
    }
}

export const edit=()=>{
    return {
        type: 'EDIT'
    }
}
